/// >>> sort_third(vec![5, 6, 3, 4, 8, 9, 2])
/// vec![2, 6, 3, 4, 8, 9, 5]
fn sort_third(l: Vec<isize>) -> Vec<isize> {

canonical_solution:
    let mut third = vec![];
    let mut out:Vec<i32> = vec![];

    for (indx,elem) in l.iter().enumerate(){
        if indx%3 == 0 && indx != 0{
            third.push(elem)
        }
    }
    third.sort();
    let mut indx_t:usize = 0;

    for i in 0..l.len() {
        if i%3 == 0 && i != 0{
            if indx_t < third.len(){
                out.push(*third[indx_t]);
                indx_t += 1;
        }
        }else{
            out.push(l[i]);
        }

    }
    return out;
}

