fn by_length(arr: Vec<isize>) -> Vec<String> {

canonical_solution:
    let mut res: Vec<String> = vec![];
    let mut arr_cp: Vec<i32> = arr.clone();
    arr_cp.sort();
    arr_cp.reverse();
    let map: HashMap<i32, &str> = HashMap::from([
