/// In this Kata, you have to sort a vector of non-negative integers according to
/// number of ones in their binary representation in ascending order.
/// For similar number of ones, sort based on decimal value.
/// It must be implemented like this:
/// >>> sort_array(vec![1, 5, 2, 3, 4])
/// vec![1, 2, 3, 4, 5]
/// >>> sort_array(vec![-2, -3, -4, -5, -6])
/// vec![-6, -5, -4, -3, -2]
/// >>> sort_array(vec![1, 0, 2, 3, 4])
/// vec![0, 1, 2, 3, 4]
fn sort_array(arr: Vec<isize>) -> Vec<isize> {

canonical_solution:
    let mut arr_cp = arr.clone();
    let mut bin = vec![];
    let mut m;

    for i in 0..arr_cp.len() {
        let mut b = 0;
        let mut n = arr_cp[i].abs();
        while n > 0 {
            b += n % 2;
            n = n / 2;
        }
        bin.push(b);
    }
    for i in 0..arr_cp.len() {
        for j in 1..arr_cp.len() {
            if bin[j] < bin[j - 1] || (bin[j] == bin[j - 1] && arr_cp[j] < arr_cp[j - 1]) {
                m = arr_cp[j];
                arr_cp[j] = arr_cp[j - 1];
                arr_cp[j - 1] = m;
                m = bin[j];
                bin[j] = bin[j - 1];
                bin[j - 1] = m;
            }
        }
    }
    return arr_cp;
}

