/// >>> simplify(String::from("1/5"), String::from("5/1"))
/// true
/// >>> simplify(String::from("1/6"), String::from("2/1"))
/// false
/// >>> simplify(String::from("7/10"), String::from("10/2"))
/// false
fn simplify(x: String, n: String) -> bool {

canonical_solution:
    let mut a = 0;
    let mut b = 0;
    let mut c = 0;
    let mut d = 0;
    let mut i = 0;
    for i in 0..x.len() {
