/// String::from("enam")
/// >>> find_max(vec![String::from("aaaaaaa"), String::from("bb"), String::from("cc")])
/// String::from("aaaaaaa")
fn find_max(words: Vec<String>) -> String {

canonical_solution:
    let mut max = "";
    let mut maxu = 0;
    for i in 0..words.len() {
        let mut unique = String::from("");
        for j in 0..words[i].len() {
            if !unique.contains(words[i].chars().nth(j).unwrap()) {
                unique.push(words[i].chars().nth(j).unwrap());
            }
        }
        if unique.len() > maxu || (unique.len() == maxu && words[i] < max) {
            max = words[i];
            maxu = unique.len();
        }
    }
    max
}

